import React, { useState } from 'react';
// import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import {
    CssBaseline,
    Box,
    Container,
    Button,
    Typography,
    createTheme,
    ThemeProvider,
    TextField,
    Select,
    MenuItem, 
    FormControl,
    InputLabel,
} from '@material-ui/core';
import axios from 'axios';
//import Alert from '@mui/material/Alert';
import { useHistory } from 'react-router-dom';
import BarcodeScannerComponent from 'react-webcam-barcode-scanner2';

const Scanner = (props) => {
    const [data, setData] = useState('Not Found');
    const [testResult, setTestResult] = useState('');
    const history = useHistory();

    const loginId = history.location.state.loginId;


    const theme = createTheme();

    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.currentTarget);
        // eslint-disable-next-line no-console

        console.log({
            testID: formData.get('testId'),
            testResult: formData.get('testResult'),
            loginId: formData.get('userId')
        });

        // const json = JSON.stringify({
        //     EinsteinID: formData.get('userId'),
        //     CovidTestCode: formData.get('testId'),
        //     TestResult: formData.get('testResult')
        // });
        axios({
            method: 'post',
            url: 'http://localhost:5000/covid/new',
            data: formData,
            headers: {"Content-Type": "multipart/form-data"},
        })
        .then((response) => {
            console.log(response);
        }, (error) => {
            console.log(error)
        });
    };

    const handleChange = (event) => {
      setTestResult(event.target.value);
      console.log(testResult);
    };

    const useStyles = makeStyles((theme) => ({
        formControl: {
          margin: theme.spacing(1),
          minWidth: 120,
        },
        selectEmpty: {
          marginTop: theme.spacing(2),
        },
      }));

    const classes = useStyles()

    return (
        <div className="pageLook">
            <BarcodeScannerComponent 
            width={300} 
            height={300}
            onUpdate={(err, result) => {
                if (result) setData(result.text)
            }}
            />

            <ThemeProvider theme={theme}>
                <Container component="main" maxWidth="xs">
                    <CssBaseline />
                    <Box
                    sx={{
                        marginTop: 8,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                    }}
                    >
                    <Typography component="h1" variant="h5">
                        Scan
                    </Typography>
                    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
                        <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="testId"
                        label="Covid-19 Test ID"
                        name="testId"
                        autoFocus
                        value={data}
                        />
                        <TextField
                        type="hidden"
                        id="userId"
                        name="userId"
                        value={loginId}
                        />
                        <FormControl style={{ width: '100%' }} className={classes.formControl}>
                            <InputLabel id="test">Test Result</InputLabel>
                            <Select
                            labelId="test"
                            id="testResult"
                            name="testResult"
                            value={testResult}
                            onChange={handleChange}
                            label="Test Result"
                            fullWidth
                            >
                                <MenuItem value={'None'}>
                                    <em>None</em>
                                </MenuItem>
                                <MenuItem value={'Negative'}>Negative</MenuItem>
                                <MenuItem value={'Positive'}>Positive</MenuItem>
                                <MenuItem value={'Invalid'}>Invalid</MenuItem>
                            </Select>
                        </FormControl>

                        <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{ mt: 3, mb: 2 }}
                        >
                        Submit
                        </Button>
                    </Box>
                    </Box>
                </Container>
            </ThemeProvider>

        </div>
    );
}




export default Scanner;
